import React, { useEffect, useState } from 'react'
import Table, { UnivTable } from '../Table'
import { Link } from 'react-router-dom'
import { useSellerId } from '../../Theme1/Contexts/SellerContext'
import axios from 'axios'
import { useToken } from '../../Admin-S/Contexts/token'
function Inventory() {
    let Sid=useSellerId()
    const [isLoading, setisLoading] = useState(true)
    const [Products, setProducts] = useState({
    })
    let headers = useToken()
    useEffect(() => {
       axios.get("/api/findSellerProducts/"+Sid,{headers: headers}).then(data=>{
           console.log(data.data)

           setProducts(data.data)
           setisLoading(false)
       })
    }, [])
    const Delete = (row) =>{
       axios.put("/api/DeleteProducts/"+Sid,{id : row._id}).then(()=>{
           alert('Delete Successly')
           window.location.reload()
       })
    }
    if(isLoading) {
        return (
            <div>Loading</div>
        )
    }
    else {
    return (

        <div className="w-full  m-10 flex">
        <h1 className="text-3xl m-2 text-left">Products</h1>
        <Link to="add" className=" max-h-10 p-2 rounded-lg bg-lightgreen" >Add +</Link> 
            <div className=" overflow-x-scroll max-w-screen-lg">
            <UnivTable Responses={Products} Delete={Delete}/>  
            </div>
        
    </div>
    )
}
}

export default Inventory
