import React from 'react'
import { Link } from "react-router-dom";

const NavItem = ({ navItem }) => {
    return (
      <li className="px-4  py-2.5  flex items-centre cursor-pointer  ">
        <Link className="nav-link"  to={`/${navItem.link}`}>
          {navItem.text} <span className="sr-only">(current)</span>
        </Link>
      </li>
    );
  };


export default function Sidebar() {
    return (
        <div className="  flex sidebar ">
            <div className=" flex flex-wrap bg-red-50 h-screen  h-30 position: sticky top-50">
            <div className="bg-red-50 px-4">
                <h1 className="text-4xl my-7">LOGO</h1>
                <div className="pt-2 mb-5 bg-red-50 rounded-md ">
                    <h3 className=" text-2xl  font-bold text-gray-400">
                        DashBoard
                    </h3>
                    <ul className="sticky p-5   bg-red-50 text-lg  list-none">

                        {[
              {
                link: "website",
                text: "Website",
              },
              {
                link: "inventory",
                text: "Inventory",
              },
              {
                link: "sales",
                text: "Sales",
              },
              {
                link: "customers",
                text: "Customers",
              },
              {
                link: "bills",
                text: "Bills",
              },
              {
                link: "promocodes",
                text: "Promo Codes",
              },
              {
                link: "settings",
                text: "Settings",
              },
              {
                link: "logout",
                text: "Logout",
              }
            ].map((navItem, index) => {
              return <NavItem key={index} navItem={navItem} />;
            })}
                    </ul>
                </div>
                <div>
                <ul className="sticky p-5  text-left bg-red-50 text-md  list-none">
                    <li>Privacy Statement</li>
                    <li>Privacy Statement</li>
                    <li>Terms Of Use</li>
                    <li>Contact Us</li>
                    </ul>

                </div>
            </div>   
        </div>
        </div>

    )
}
