import { useState } from 'react'
import { StarIcon } from '@heroicons/react/solid'
import { RadioGroup } from '@headlessui/react'
import { Rating } from 'react-simple-star-rating'
import { useCart, useCartUpdate } from '../../Contexts/CartContext'

function CheckIcon(props) {
    return (
      <svg viewBox="0 0 24 24" fill="none" {...props}>
        <circle cx={12} cy={12} r={12} fill="#fff" opacity="0.2" />
        <path
          d="M7 13l3 3 7-7"
          stroke="#fff"
          strokeWidth={1.5}
          strokeLinecap="round"
          strokeLinejoin="round"
        />
      </svg>
    )
  }

  const plans = [
    {
      name: 'Startup',
      ram: '12GB',
      cpus: '6 CPUs',
      disk: '160 GB SSD disk',
    },
    {
      name: 'Business',
      ram: '16GB',
      cpus: '8 CPUs',
      disk: '512 GB SSD disk',
    },
    {
      name: 'Enterprise',
      ram: '32GB',
      cpus: '12 CPUs',
      disk: '1024 GB SSD disk',
    },
  ]

export default function ProductPage() {
  const products = useCart()
  let product = products[0]
  const CartUpdate = useCartUpdate()
  console.log(CartUpdate)
  const [rating, setRating] = useState(2) // initial rating value
  const [selected, setSelected] = useState(plans[0])
  const [qty, setQty] = useState(1);
  return (
    <div className="bg-white">
         <nav aria-label="Breadcrumb">
          <ol role="list" className="max-w-2xl mx-auto px-4 flex items-center space-x-2 sm:px-6 lg:max-w-7xl lg:px-8">
            <li className="text-sm">
              <a href={product.href} aria-current="page" className="font-medium text-gray-500 hover:text-gray-600">
                {product.name}
              </a>
            </li>
          </ol>
        </nav>
      <div className="pt-6 flex flex-row">

        {/* Image gallery */}
        <div className="grid grid-cols-1 w-1/2">
          <div className="hidden aspect-w-3 m-auto w-3/4 mt-5 aspect-h-4 rounded-lg overflow-hidden lg:block">
            <img
              src={product.imageSrc}
              alt={product.imageAlt}
              className="w-full h-full object-center object-cover"
            />
          </div>
            {/* <div className="aspect-w-3 w-3/4 m-auto mt-5 aspect-h-2 rounded-lg overflow-hidden">
              <img
                src={product.images[1].src}
                alt={product.images[1].alt}
                className="w-full h-full object-center object-cover"
              />
            </div> */}
            {/* <div className="aspect-w-3 w-3/4  m-auto mt-5 aspect-h-2 rounded-lg overflow-hidden">
              <img
                src={product.images[2].src}
                alt={product.images[2].alt}
                className="w-full h-full object-center object-cover"
              />
          </div> */}
        </div>

        {/* Product info */}
        <div className="max-w-2xl text-left flex-1 pt-10 pb-16 px-4 sm:px-6 lg:max-w-7xl lg:pt-16 lg:pb-24 lg:px-8 ">
          <div className="lg:col-span-2 lg:pr-8">
            <h1 className="text-2xl font-extrabold tracking-tight text-left text-gray-900 sm:text-3xl">{product.name}</h1>
           <div className="text-left mt-2">
           <Rating ratingValue={rating} transition={0} />
           </div>
          </div>

          <div className="py-10 lg:pt-6 lg:pb-16 lg:col-start-1 lg:col-span-2  lg:pr-8">
            {/* Description and details */}
            <div>
              <h3 className="sr-only">Description</h3>

              <div className="space-y-6">
                <p className="text-base text-gray-900">{product.description}</p>
              </div>
            </div>

            <div className="mt-10">
              <h2 className="text-sm font-medium text-gray-900">Details</h2>
              <div className="mt-4 lg:mt-0 lg:row-span-3">
            <h2 className="sr-only">Product information</h2>
            <p className="text-3xl text-gray-900">{product.price}</p>


            <form className="mt-10">
              {/* Colors */}
              <div>
                <h3 className="text-sm text-gray-900 font-medium">Variants</h3>
                <div className="w-full px-4 py-16">

      <div className="w-full max-w-md mx-auto">
        <RadioGroup value={selected} onChange={setSelected}>
          <RadioGroup.Label className="sr-only">Variants</RadioGroup.Label>
          <div className="space-y-2">
            {plans.map((plan) => (
              <RadioGroup.Option
                key={plan.name}
                value={plan}
                className={({ active, checked }) =>
                  `${
                    active
                      ? 'ring-2 ring-offset-2 ring-offset-sky-300 ring-white ring-opacity-60'
                      : ''
                  }
                  ${
                    checked ? ' bg-theme bg-opacity-75 text-w' : 'bg-white'
                  }
                    relative rounded-lg shadow-md px-5 py-4 cursor-pointer flex focus:outline-none`
                }
              >
                {({ active, checked }) => (
                  <>
                    <div className="flex items-center justify-between w-full">
                      <div className="flex items-center">
                        <div className="text-sm">
                          <RadioGroup.Label
                            as="p"
                            className={`font-medium  ${
                              checked ? 'text-white' : 'text-gray-900'
                            }`}
                          >
                            {plan.name}
                          </RadioGroup.Label>
                          <RadioGroup.Description
                            as="span"
                            className={`inline ${
                              checked ? 'text-sky-100' : 'text-gray-500'
                            }`}
                          >
                            <span>
                              {plan.ram}/{plan.cpus}
                            </span>{' '}
                            <span aria-hidden="true">&middot;</span>{' '}
                            <span>{plan.disk}</span>
                          </RadioGroup.Description>
                        </div>
                      </div>
                      {checked && (
                        <div className="flex-shrink-0 text-white">
                          <CheckIcon className="w-6 h-6" />
                        </div>
                      )}
                    </div>
                  </>
                )}
              </RadioGroup.Option>
            ))}
          </div>
        </RadioGroup>
      </div>
    </div>
              </div>
              <div className="container">
      <div className="center">
          <>
            <div className="card">
              <div className="flex">
                <button onClick={e=>{
                    e.preventDefault()
                    if(qty-1 > 0) {setQty(qty-1)}
                    }} className="border p-2 mx-5 text-4xl">
                  -
                </button>
                <div className="text-4xl p-2 ">{qty}</div>
                <button onClick={e=>{
                    e.preventDefault()
                    setQty(qty+1)}} className="border p-2 mx-5 text-4xl">
                  +
                </button>
              </div>
            </div>
          </>
      </div>
    </div>
              <button
                type="submit"
                onClick={e=>{
                    e.preventDefault()
                    for(let i=0; i<qty; ++i) 
                    {
                        console.log('trying')
                        CartUpdate.AddProduct(product)
                    }
                }}
                className="mt-10 w-full bg-theme text-w border border-transparent rounded-md py-3 px-8 flex items-center justify-center text-base font-medium text-white hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500" >
                Add to bag
              </button>
            </form>
          </div>

            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
